mapper.select:
select count(a.id) from crm.tbl_activity a join crm.tbl_user u on a.owner = u.id
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.startDate < #{endDate}

select a.id,a.name,u.name as owner,a.startDate,a.endDate from crm.tbl_activity a join crm.tbl_user u on a.owner = u.id
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.startDate < #{endDate}
order by a.createTime desc

mapper.update:
update crm.tbl_activity set owner=#{owner}, name=#{name}, startDate=#{startDate},
                            endDate=#{endDate},cost=#{cost}, description=#{description},
                            editBy=#{editBy},editTime=#{editTime} where id=#{id}
